#include <set>
#include <map>
#include <algorithm>
#include <string>
#include <cmath>
#include <cstring>
#include <queue>
#include <ctime>

using namespace std;

#define pii pair<int, int>
#define mp make_pair

typedef long long ll;

class Solution {
public:
    int numDistinct(string s, string t) {
		
		dp.clear();
		dp.resize(t.size() + 1);
		for (int i = 0; i < t.size(); i++)
		{
			dp[i].clear();
			dp[i].insert(dp[i].begin(), s.size() + 1, 0);
		}
		dp[t.size()].insert(dp[t.size()].begin(), s.size() + 1, 1);
		
		int res = 0, i, j;
		for (i = t.size() - 1; i >= 0; i--)
		{
			for (j = s.size() - 1; j >= 0; j--)
			{
				dp[i][j] = dp[i][j + 1];
				if (t[i] == s[j])
				{
					dp[i][j] += dp[i + 1][j + 1];
				}
			}
		}
		
		return dp[0][0];
    }
	
	vector<vector<int> > dp;
};

int main()
{
	string a = "";
	string b = "rwmimatmhydhbujebqehjprarwfkoebcxxqfktayaaeheys";
	//string b = "aa";
	Solution S;
	cout << S.numDistinct(a, b) << endl;
	//cout << S.cnt << endl;
	return 0;
}