/******************************************************************************************
Distinct Subsequences
******************************************************************************************/


#include <iostream>
#include <cstdio>
#include <vector>
#include <set>
#include <map>
#include <algorithm>
#include <string>
#include <cmath>
#include <cstring>
#include <queue>
#include <ctime>

using namespace std;
#define pii pair<int, int>
#define mp make_pair

typedef long long ll;

class Solution {
public:
    int numDistinct(string s, string t) {
		cnt1 = cnt2 = 0;
		dp.clear();
		return solve(s, t, 0, 0);
    }
	
	int solve(const string &s, const string &t, int i, int j)
	{
		
		if (j >= t.size())
		{
			return 1;
		}
		if (t.size() - j > s.size() - i)
		{
			return 0;
		}
		cnt1++;
		map<pii, int>::iterator iter;
		if ((iter = dp.find(mp(i, j))) != dp.end())
		{
			return iter->second;
		}
		
		int res = 0;
		for (int k = i; k < s.size(); k++)
		{
			cnt2++;
			if (s[k] == t[j])
			{
				res += solve(s, t, k + 1, j + 1);
			}
		}
		
		dp[mp(i, j)] = res;
		return res;
	}
	int cnt1, cnt2;
	map<pii, int> dp;
};

int main()
{
	string a = "";
	string b = "rwmimatmhydhbujebqehjprarwfkoebcxxqfktayaaeheys";
	//string b = "aa";
	Solution S;
	cout << S.numDistinct(a, b) << endl;
	cout << S.cnt1 << endl << S.cnt2 << endl;
	return 0;
}