/******************************************************************************************
Distinct Subsequences
******************************************************************************************/


#include <iostream>
#include <cstdio>
#include <vector>
#include <set>
#include <map>
#include <algorithm>
#include <string>
#include <cmath>
#include <cstring>
#include <queue>
#include <ctime>

using namespace std;

#define pii pair<int, int>
#define mp make_pair

typedef long long ll;

class Solution {
public:
    int numDistinct(string s, string t) {
		cnt = 0;
		dp.clear();
		return solve(s, t, 0, 0);
    }
	
	int solve(const string &s, const string &t, int i, int j)
	{
		
		if (j >= t.size())
		{
			return 1;
		}
		if (t.size() - j > s.size() - i)
		{
			return 0;
		}
		cnt++;
		map<pii, int>::iterator iter;
		if ((iter = dp.find(mp(i, j))) != dp.end())
		{
			return iter->second;
		}
		
		int res = 0;

		if (s[i] == t[j])
		{
			res += solve(s, t, i + 1, j + 1);
		}
		res += solve(s, t, i + 1, j);
		
		
		dp[mp(i, j)] = res;
		return res;
	}
	
	map<pii, int> dp;
	ll cnt;
};

int main()
{
	string a = "";
	string b = "rwmimatmhydhbujebqehjprarwfkoebcxxqfktayaaeheys";
	//string b = "aa";
	Solution S;
	cout << S.numDistinct(a, b) << endl;
	cout << S.cnt << endl;
	return 0;
}