#define pii pair<int, int>
#define mp make_pair

typedef long long ll;

class Solution {
public:
    bool isMatch(string s, string p) {
        int i, j, preI, preJ;
		bool hasPre = false;
		for (i = 0, j = 0; i < s.size();)
		{
			if (j < p.size())
			{
				if (s[i] == p[j] || p[j] == '?')
				{
					i++;
					j++;
				}
				else if (p[j] == '*')
				{
					while (j + 1 < p.size() && p[j + 1] == '*')
					{
						j++;
					}
					preJ = j;
					preI = i;
					hasPre = true;
					j++;
				}
				else if (hasPre)
				{
					i = preI + 1;
					j = preJ;
				}
				else
				{
					return false;
				}
			}
			else if (hasPre)
			{
				i = preI + 1;
				j = preJ;
			}
			else
			{
				return false;
			}
		}
		
		while (j < p.size() && p[j] == '*')
		{
			j++;
		}		
		return j == p.size();
    }
};


